document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("like-button"),n=document.getElementById("like-count"),s=document.getElementById("like-emoji"),o=`likeCount-${window.location.pathname}`;let e=localStorage.getItem(o)||0;n.textContent=e,t?t.addEventListener("click",()=>{e++,n.textContent=e,localStorage.setItem(o,e),s.style.transform="scale(1.3)",setTimeout(()=>{s.style.transform="scale(1)"},200)}):console.error("Like button element not found!")})